import { toast } from "react-toastify";

export const handleSuccess = (message) => {
  toast.success(message, {
    position: toast.POSITION.TOP_CENTER, // ✅ Centered at the top
  });
};

export const handleError = (message) => {
  toast.error(message, {
    position: toast.POSITION.TOP_CENTER, // ✅ Centered at the top
  });
};
