import React from "react";
import logo from "./assects/logo.png";
import { Link } from "react-router-dom";
import { useAuth } from "./AuthContext";

function Navbar() {
  const { user, logout } = useAuth(); // Get user and logout from context

  const token = user?.token;
  const role = user?.role;

  return (
    <div>
      <ul>
        <li>
          <Link
            className="navbar-brand mt-3"
            to="/"
            style={{ fontSize: "larger", color: "aliceblue" }}
          >
            <img
              src={logo}
              width="30"
              height="30"
              className="d-inline-block align-top"
              alt="logo"
            />{" "}
            EventsOn
          </Link>
        </li>
        <li>
          <Link className="active" to="/">
            Home
          </Link>
        </li>
        <li>
          <Link to="/eventspage">Events</Link>
        </li>

        {token && (
          <li>
            <Link to="/schedulepage">Schedule</Link>
          </li>
        )}

        {token && role === "admin" && (
          <>
            <li>
              <Link to="/requestspage">Requests</Link>
            </li>
            <li>
              <Link to="/reportspage">Reports</Link>
            </li>
          </>
        )}

        {!token ? (
          <li>
            <Link to="/loginpage">Login</Link>
          </li>
        ) : (
          <li>
            <button
              type="button"
              className="btn btn-dark"
              style={{
                padding: "8px 16px",
                fontSize: "16px",
                borderRadius: "50px",
                color: "white",
                backgroundColor: "black",
                border: "none",
                cursor: "pointer",
                margin: "11px 0px",
              }}
              onClick={() => {
                logout(); // logout via context
                window.location.reload(); // reload to update navbar visibility
              }}
            >
              Logout
            </button>
          </li>
        )}
      </ul>
    </div>
  );
}

export default Navbar;
