import { useLocation } from "react-router-dom";

function Navbar() {
  const { user, logout } = useAuth();
  const token = user?.token;
  const role = user?.role;
  const location = useLocation(); // get current path

  return (
    <div>
      <ul>
        <li>
          <Link
            className="navbar-brand mt-3"
            to="/"
            style={{ fontSize: "larger", color: "aliceblue" }}
          >
            <img
              src={logo}
              width="30"
              height="30"
              className="d-inline-block align-top"
              alt="logo"
            />{" "}
            EventsOn
          </Link>
        </li>
        <li>
          <Link
            to="/"
            className={location.pathname === "/" ? "active-link" : ""}
          >
            Home
          </Link>
        </li>
        <li>
          <Link
            to="/eventspage"
            className={location.pathname === "/eventspage" ? "active-link" : ""}
          >
            Events
          </Link>
        </li>
        {token && (
          <li>
            <Link
              to="/schedulepage"
              className={location.pathname === "/schedulepage" ? "active-link" : ""}
            >
              Schedule
            </Link>
          </li>
        )}
        {token && role === "admin" && (
          <>
            <li>
              <Link
                to="/requestspage"
                className={location.pathname === "/requestspage" ? "active-link" : ""}
              >
                Requests
              </Link>
            </li>
            <li>
              <Link
                to="/reportspage"
                className={location.pathname === "/reportspage" ? "active-link" : ""}
              >
                Reports
              </Link>
            </li>
          </>
        )}
        {!token ? (
          <li>
            <Link
              to="/loginpage"
              className={location.pathname === "/loginpage" ? "active-link" : ""}
            >
              Login
            </Link>
          </li>
        ) : (
          <li>
            <button
              type="button"
              className="btn btn-dark"
              style={{
                padding: "8px 16px",
                fontSize: "16px",
                borderRadius: "50px",
                color: "white",
                backgroundColor: "black",
                border: "none",
                cursor: "pointer",
                margin: "11px 0px",
              }}
              onClick={() => {
                logout();
                window.location.reload();
              }}
            >
              Logout
            </button>
          </li>
        )}
      </ul>
    </div>
  );
}
